dict.string:
#!/bin/bash
set -e
aws-vault() { "${aws_vault_path:-/usr/local/bin/aws-vault}" "$@" }
function fetch(){ result=$(aws-vault list) if [[ $? -ne 0 ]]; then echo "error on running aws-vault list" echo "${result}" >&2 exit 1 fi echo "${result}" }
function parse(){ local aws_vault_result="$1" echo "${aws_vault_result}" | tail +3 | cut -f 1 -d' ' | grep -Ev '^-$' }
function format_alfred(){ local profiles="$1" local profiles_max_lines=$(echo -n "${profiles}" | grep -c '^') local i=0
echo '{"items": [' for profile in ${profiles}; do cat << EOS { "uid": "${profile}", "title": "${profile}", "arg": "${profile}", "icon": {"path": "icon.png"}, "autocomplete": "${profile}" EOS i=$(( i+1 )) if [[ $i -eq $profiles_max_lines ]]; then echo ' }' else echo ' },' fi done echo ']}'
}
aws_vault_result=$(fetch) profiles=$(parse "${aws_vault_result}") format_alfred "${profiles}"

aws_account=${1:-$default_profile}
aws_account=${aws_account// /} # trim space

AWS_ASSUME_ROLE_TTL=${AWS_ASSUME_ROLE_TTL:-1h}
AWS_FEDERATION_TOKEN_TTL=${AWS_FEDERATION_TOKEN_TTL:-1h}

aws-vault() {
  "${aws_vault_path:-/usr/local/bin/aws-vault}" "$@"
}

firefox-bin() {
  "${firefox_path:-/Applications/Firefox.app/Contents/MacOS/firefox}" "$@"
}

login_url="$(aws-vault login "$aws_account" --prompt=osascript --stdout)"

if [[ $? -ne 0 ]] then;
	osascript -e 'display dialog "AWS Authentication failed"'
	echo ${login_url} > /tmp/aws-vault-login-${aws_account}-$(date +"%Y%m%d_%H%M%S").log
	exit 1
fi

if [[ ${preferred_browser} == "firefox" ]]; then
	open -na firefox --args --profile $HOME/Library/Application\ Support/Firefox/Profiles/aws-vault/${aws_account} ${login_url}

elif [[ ${preferred_browser} == "chrome" ]]; then
	open -na "Google Chrome" --args --user-data-dir=$HOME/Library/Application\ Support/Google/Chrome/aws-vault/${aws_account} ${login_url}

elif [[ ${preferred_browser} == "firefox-containers" ]]; then
  # to work install add-on:
  # https://addons.mozilla.org/en-US/firefox/addon/open-url-in-container/

  ENCODED_URL="${login_url//&/%26}"
  URI_HANDLER="ext+container:name=${aws_account}&url=${ENCODED_URL}"

  firefox-bin "${URI_HANDLER}"
fi
